import React from 'react';
import { motion } from 'framer-motion';

const Header: React.FC = () => {
  return (
    <motion.header 
      className="bg-white border-b border-secondary-200 px-6 py-4"
      initial={{ y: -50, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.3 }}
    >
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-primary-500 rounded-lg flex items-center justify-center">
            <span className="text-white font-bold text-sm">W</span>
          </div>
          <div>
            <h1 className="text-xl font-bold text-secondary-800">
              Websites.co.in Builder
            </h1>
            <p className="text-sm text-secondary-600">
              Drag & Drop Website Creator
            </p>
          </div>
        </div>
        
        <div className="flex items-center space-x-4">
          <motion.button
            className="px-4 py-2 text-sm font-medium text-secondary-600 hover:text-secondary-800 transition-colors"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Preview
          </motion.button>
          <motion.button
            className="px-4 py-2 bg-primary-500 text-white rounded-lg text-sm font-medium hover:bg-primary-600 transition-colors"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            Save
          </motion.button>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
